import React, { useState } from "react";

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    // Basculer la classe dark sur le body pour activer les styles Tailwind CSS
    document.documentElement.classList.toggle("dark");
  };

  return (
    <nav className="flex items-center justify-between px-4 py-2">
      {/* Logo */}
      <div className="flex items-center">
        <img src="/logo.png" alt="Logo" className=" mr-2" />
      </div>

      {/* Ic√¥nes */}
      <div className="flex items-center space-x-4">
        {/* Bouton pour changer de langue */}
        <button
          className="text-gray-600 hover:text-gray-800 dark:text-gray-300 dark:hover:text-gray-100"
          onClick={() => alert("Changer de langue (√† impl√©menter)")}
        >
          üåê
        </button>

        {/* Bouton pour mode clair/sombre */}
        <button
          className="text-gray-600 hover:text-gray-800 dark:text-gray-300 dark:hover:text-gray-100"
          onClick={toggleDarkMode}
        >
          {darkMode ? "üåû" : "üåô"}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
