import React, { useState } from "react";
import SearchBar from "./components/SearchBar";
import ResultsList from "./components/ResultsList";

const App = () => {
  // Données statiques pour les résultats de recherche
  const staticData = [
    { title: "Document 1", snippet: "Ceci est un extrait du document 1." },
    { title: "Document 2", snippet: "Ceci est un extrait du document 2." },
    { title: "Document 3", snippet: "Ceci est un extrait du document 3." },
  ];

  const [results, setResults] = useState([]);

  const fetchResults = (query) => {
    // Filtrer les données statiques en fonction de la requête
    const filteredResults = staticData.filter((doc) =>
      doc.title.toLowerCase().includes(query.toLowerCase())
    );
    setResults(filteredResults);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <h1 className="mb-8 text-3xl font-bold text-center text-blue-600">
        Moteur de Recherche de Documents
      </h1>
      <SearchBar onSearch={fetchResults} />
      <ResultsList results={results} />
    </div>
  );
};

export default App;
