import React, { useState } from "react";
import { AiOutlineGlobal } from "react-icons/ai"; // Icône de globe
import { FiSun, FiMoon } from "react-icons/fi"; // Icônes clair/sombre

const Navbar = () => {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    // Basculer la classe dark sur le body pour activer les styles Tailwind CSS
    document.documentElement.classList.toggle("dark");
  };

  return (
    <nav className="flex items-center justify-between px-4 py-2">
      {/* Logo */}
      <div className="flex items-center">
        <img src="/logo.png" alt="Logo" className="h-16 w-24 mr-2" />
      </div>

      {/* Icônes */}
      <div className="flex items-center space-x-4">
        {/* Bouton pour changer de langue */}
        <button
          className="text-gray-600 hover:text-gray-800 dark:text-gray-300 dark:hover:text-gray-100"
          onClick={() => alert("Changer de langue (à implémenter)")}
        >
          <AiOutlineGlobal className="text-2xl" />
        </button>

        {/* Bouton pour mode clair/sombre */}
        <button
          className="text-gray-600 hover:text-gray-800 dark:text-gray-300 dark:hover:text-gray-100"
          onClick={toggleDarkMode}
        >
          {darkMode ? (
            <FiSun className="text-2xl" />
          ) : (
            <FiMoon className="text-2xl" />
          )}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
